import React from 'react';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';

function App() {

    let isEditProfilePopupOpen = false;


    function handleEditAvatarClick () {
        const popupEditAvatar = document.querySelector('.popup_type_edit-photo');
        popupEditAvatar.classList.add('popup_opened');
    }

    function handleEditProfileClick () {
        const popupEditInfo = document.querySelector('.popup_type_edit');
        popupEditInfo.classList.add('popup_opened');
    }

    function handleAddPlaceClick () {
        const popupAdd = document.querySelector('.popup_type_add');
        popupAdd.classList.add('popup_opened');
    }

  return (
    <div className="App">
      <div class="page">
      <div class="page__container">
      <Header/>
      <Main
          onEditAvatar = {handleEditAvatarClick}
          onEditProfile = {handleEditProfileClick}
          onAddPlace = {handleAddPlaceClick}
      />
      <Footer/>

        <template class="elements-template">
        <li class="element">
            <img src="#" class="element__image" alt="изображение на карточке"/>
            <button class="element__delete-button" type="button"></button>
            <div class="element__description">
                <h2 class="element__text"></h2>
                <div class="element__like-area">
                    <button class="element__like" type="button"></button>
                    <p class="element__like-count"></p>
                </div>
            </div>
        </li>
    </template>
    </div>
    </div>
    </div>
  );
}

export default App;
